import React, { useState, useEffect } from 'react';
import axios from '../api/axiosConfig';
import '/';

const Analytics: React.FC = () => {
  const [analytics, setAnalytics] = useState<any>(null);

  useEffect(() => {
    const fetchAnalytics = async () => {
      const response = await axios.get('/analytics');
      setAnalytics(response.data);
    };
    fetchAnalytics();
  }, []);

  if (!analytics) return <div className="loading">Loading...</div>;

  return (
    <div className="container analytics">
      <h1>Analytics</h1>
      <p>Number of people currently in the building: {analytics.peopleInside}</p>
      <p>Average duration of stay: {analytics.averageDuration} minutes</p>
      <p>Peak entry times: {JSON.stringify(analytics.peakEntryTimes)}</p>
      <p>Peak exit times: {JSON.stringify(analytics.peakExitTimes)}</p>
      <p>Most frequently used entry gates: {JSON.stringify(analytics.entryGates)}</p>
      <p>Most frequently used exit gates: {JSON.stringify(analytics.exitGates)}</p>
    </div>
  );
};

export default Analytics;
